    private class MonitorDataHandler extends Handler {
        public MonitorDataHandler(Looper looper) {
            super(looper);
        }

        @Override
        public void handleMessage(Message msg) {
            super.handleMessage(msg);


            while (isdownloadMonitorData) {
                //     //        模拟监护仪返回的数据
                final int randomInt = (int) (Math.random() * 5);
//                Log.e("更新数据代号", randomInt + "");
                byte[] monitorBytes = SIMULATIONMONITORDATA.get(randomInt);
                waveDataHelper.saveOrUpdateWaveInfo(monitorBytes);
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        hr_textview.setText(randomInt+"");
                        refreshOtherDatas();
                    }
                });
//                Log.e("get_monitor_data", "" + Thread.currentThread().getName());
                try {
                    Thread.sleep(3000);
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
            }


        }
    }

    private void initDatas() {
        waveDataHelper = WaveDataHelper.getInstance();
        HandlerThread dataThreadHandler = new HandlerThread("get_monitor_data");
        dataThreadHandler.start();
        monitorDataHandler = new MonitorDataHandler(dataThreadHandler.getLooper());

        SIMULATIONMONITORDATA.add(TESTBYTE_101);
        SIMULATIONMONITORDATA.add(TESTBYTE_119);
        SIMULATIONMONITORDATA.add(TESTBYTE_118);
        SIMULATIONMONITORDATA.add(TESTBYTE_120);
        SIMULATIONMONITORDATA.add(TESTBYTE_102);

    }

