    private void refreshMonitor() {
        MonitorBean monitorBean = WaveDataHelper.getInstance().getWaveInfo();
        WaveFormBeanShell waveFormBeanShell = monitorBean.getWave_01();
        surfacetest_view.refreshWave(waveFormBeanShell.getWaveFormBeanList());
        StringBuffer sb = new StringBuffer();
        sb.append("RESP").append(":").append(monitorBean.getResp());
//                .append("\n").append("PR:").append(monitorBean.getPr());
        tztxt_textview.setText(sb.toString());
    }
